package com.sabourazadani;

public class PatternsAreFun {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i = 1; i <=5; i++) {
			for(int j = i; j >= 1; j--) {
				System.out.print(j + " ");
			}
			System.out.println();
		} 
		System.out.println("^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^");
		System.out.println("^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^");
		
		for(int i = 1; i <=5; i++) {
			for(int j = i; j >= 1; j--) {
				System.out.print(j + " ");
			}
			System.out.println();
		}	
	}
}
